import React from "https://esm.sh/react@18.2.0";
import ReactDOM from "https://esm.sh/react-dom@18.2.0";
import {useState} from 'https://esm.sh/react@18.2.0';

const Button = ({ children, className, ...props }) => (
  <button className={className} {...props}>
    {children}
  </button>
);

function App() {
  const [expandedOrder, setExpandedOrder] = useState(null);
  const [activeTab, setActiveTab] = useState({}); // track tabs per order

  const toggleExpand = (orderId) => {
    setExpandedOrder(expandedOrder === orderId ? null : orderId);
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const options = { month: "short", day: "2-digit", year: "numeric" };
    const time = date.toLocaleTimeString([], { hour: "numeric", minute: "2-digit", hour12: true });
    return (
      <div>
        <div>{date.toLocaleDateString("en-US", options)}</div>
        <div>{time}</div>
      </div>
    );
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", width: "100%" }} >
      {/* Table Header */}
      <div className="orders-header">
        <div style={{ flex: 1 }}>Order</div>
        <div style={{ flex: 2 }}>Customer</div>
        <div style={{ flex: 1 }}>Status</div>
        <div style={{ flex: 1, textAlign: "center" }}>Items</div>
        <div style={{ flex: 1, textAlign: "right" }}>Total</div>
        <div style={{ flex: 1 }}>Date</div>
      </div>

      {/* Orders Rows */}
      {appsmith.model.orders.map((order) => {
        const itemCount = order.line_items.reduce((sum, item) => sum + item.quantity, 0);
        return (
          <div key={order.id} style={{ borderBottom: "1px solid #ddd" }}>
            {/* Order summary row */}
            <div
							className="order-row"
              onClick={() => toggleExpand(order.id)}
            >
              <div style={{ flex: 1 }}>{order.number}</div>
              <div style={{ flex: 2 }}>
                {order.billing.first_name} {order.billing.last_name}<br />
                {order.billing.email}<br />
                {order.billing.phone}
              </div>
              <div style={{ flex: 1 }}>{order.status}</div>
              <div style={{ flex: 1, textAlign: "center" }}>{itemCount}</div>
              <div style={{ flex: 1, textAlign: "right" }}>
                £{parseFloat(order.total).toFixed(2)}
              </div>
              <div style={{ flex: 1 }}>{formatDate(order.date_created)}</div>
            </div>

            {/* Expandable content */}
            {expandedOrder === order.id && (
              <div className="order-dropdown" >
                {/* Tabs */}
                <div style={{ display: "flex", marginBottom: "10px" }}>
                  <div
										className={`order-tab ${(activeTab[order.id] || "items") === "items" ? "active" : ""}`}
                    onClick={() => setActiveTab({ ...activeTab, [order.id]: "items" })}
                  >
                    Items
                  </div>
                  <div
                    className={`order-tab ${(activeTab[order.id] || "items") === "details" ? "active" : ""}`}
                    onClick={() => setActiveTab({ ...activeTab, [order.id]: "details" })}
                  >
                    Details
                  </div>
                </div>

                {/* Tab content */}
                {activeTab[order.id] === "items" && (
                  <div>
                    {/* Items Table */}
                    <div style={{ display: "flex", fontWeight: "bold", borderBottom: "1px solid #ddd", padding: "5px 0" }}>
                      <div style={{ flex: 3 }}>Product</div>
                      <div style={{ flex: 1 }}>SKU</div>
                      <div style={{ flex: 1, textAlign: "center" }}>Qty</div>
                      <div style={{ flex: 1, textAlign: "right" }}>Price</div>
                      <div style={{ flex: 1, textAlign: "right" }}>Total</div>
                    </div>
                    {order.line_items.map((item) => (
                      <div key={item.id} style={{ display: "flex", padding: "8px 0", borderBottom: "1px solid #eee", alignItems: "center" }}>
                        <div style={{ flex: 3, display: "flex", alignItems: "center" }}>
                          {item.image?.src && <img src={item.image.src} alt={item.name} className="product-thumbnail" />}
                          <div>{item.name}</div>
                        </div>
                        <div style={{ flex: 1 }}>{item.sku}</div>
                        <div style={{ flex: 1, textAlign: "center" }}>{item.quantity}</div>
                        <div style={{ flex: 1, textAlign: "right" }}>£{parseFloat(item.subtotal).toFixed(2)}</div>
                        <div style={{ flex: 1, textAlign: "right" }}>£{parseFloat(item.total).toFixed(2)}</div>
                      </div>
                    ))}
                  </div>
                )}

                {activeTab[order.id] === "details" && (
                  <div style={{ display: "flex", gap: "20px", flexWrap: "wrap" }}>
                    {/* Billing */}
                    <div style={{ flex: 1, minWidth: "250px" }}>
                      <h4>Billing</h4>
                      <div>{order.billing.first_name} {order.billing.last_name}</div>
                      <div>{order.billing.company}</div>
                      <div>{order.billing.address_1} {order.billing.address_2}</div>
                      <div>{order.billing.city}, {order.billing.state} {order.billing.postcode}</div>
                      <div>{order.billing.country}</div>
                      <div>Email: {order.billing.email}</div>
                      <div>Phone: {order.billing.phone}</div>
                    </div>

                    {/* Shipping */}
                    <div style={{ flex: 1, minWidth: "250px" }}>
                      <h4>Shipping</h4>
                      <div>{order.shipping.first_name} {order.shipping.last_name}</div>
                      <div>{order.shipping.company}</div>
                      <div>{order.shipping.address_1} {order.shipping.address_2}</div>
                      <div>{order.shipping.city}, {order.shipping.state} {order.shipping.postcode}</div>
                      <div>{order.shipping.country}</div>
                      <div>Phone: {order.shipping.phone}</div>
                    </div>

                    {/* Notes */}
                    <div style={{ flex: 1, minWidth: "250px" }}>
                      <h4>Order Notes</h4>
                      <div>{order.customer_note || "None"}</div>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
}

appsmith.onReady(() => {
/*
	 * This handler function will get called when parent application is ready.
	 * Initialize your component here
	 * more info - https://docs.appsmith.com/reference/widgets/custom#onready
	 */
    ReactDOM.render(<App />, document.getElementById("root"));
});
