.container {
  font-family: Arial, sans-serif;
  border: 1px solid #ddd;
  border-radius: 8px;
  overflow: hidden;
  width: 100%;
  background-color: transparent;
}

.header {
  display: flex;
  background-color: #f5f5f5;
  padding: 5px 15px;
  font-weight: bold;
  border-bottom: 1px solid #ddd;
  font-size: 15px;
}

.rows,
.rows-extra-lines {
  display: flex;
  padding: 5px 15px;
  border-bottom: 1px solid #eee;
  align-items: center;
  color: white;
  font-size: 15px;
}

.rows-extra-lines {
  background-color: rgba(255, 255, 255, 0.08);
  border-bottom: none;
  font-size: 13px;
}

.product-name {
  flex: 3;
}

.product-quantity {
  flex: 1;
  text-align: center;
}

.product-total {
  flex: 1;
  text-align: right;
}

.rows-extra-lines .product-name {
  text-align: right;
}

.rows-extra-lines .product-total {
  font-weight: bold;
}

.discount {
  color: red;
}

