import React from "https://esm.sh/react@18.2.0";
import ReactDOM from "https://esm.sh/react-dom@18.2.0";

function App() {
	return (
		<div className="container">
		{/* Header */}
			<div className="header">
				<div className="product-name">Product Name</div>
				<div className="product-quantity">Quantity</div>
				<div className="product-total">Price</div>
				<div className="product-total">Total</div>
			</div>	
			{/* Rows - Items */}
			{appsmith.model.items.map(item => (
				<div className="rows">
					<div className="product-name">{item.name}</div>
					<div className="product-quantity">{item.quantity}</div>
					<div className="product-total">£{(parseFloat(item.subtotal) / item.quantity).toFixed(2)}</div>
					<div className="product-total">£{parseFloat(item.subtotal).toFixed(2)}</div>
				</div>
			))}

			{/* Row - Subtotal */}
			<div className="rows-extra-lines">
					<div className="product-name">Subtotal:</div>
					<div className="product-total">£{parseFloat(appsmith.model.subtotal).toFixed(2)}					</div>
			</div>
			{/* Rows - Shipping */}
			{appsmith.model.shipping_lines.map(item => (
				<div className="rows-extra-lines">
					<div className="product-name">{item.method_title}:</div>
					<div className="product-total">£{parseFloat(item.total).toFixed(2)}</div>
				</div>
			))}

			{/* Row - Discount */}
			<div className="rows-extra-lines">
					<div className="product-name">Discount:</div>
					<div className="product-total discount">- £{parseFloat(appsmith.model.discount).toFixed(2)}</div>
			</div>
			{/* Row - Total VAT */}
			<div className="rows-extra-lines">
					<div className="product-name">VAT (20%):</div>
					<div className="product-total">£{parseFloat(appsmith.model.total_tax).toFixed(2)}</div>
			</div>
			{/* Row - Order Total */}
			<div className="rows-extra-lines">
					<div className="product-name">Order Total (Incl. VAT):</div>
					<div className="product-total">£{parseFloat(appsmith.model.total).toFixed(2)}</div>
			</div>
		</div>
	);
}

appsmith.onReady(() => {
	ReactDOM.render(<App />, document.getElementById("root"));
});
