import React from "https://esm.sh/react@18.2.0";
import ReactDOM from "https://esm.sh/react-dom@18.2.0";

function App() {
	return (
		<div style={{
		fontFamily: "Arial, sans-serif",
		border: "1px solid #ddd",
		borderRadius: "8px",
		overflow: "hidden",
		width: "100%",
		backgroundColor: "transparent" 
		}}>
		{/* Header */}
			<div style={{
									display: "flex",
									backgroundColor: "#f5f5f5",
									padding: "10px 15px",
									fontWeight: "bold",
									borderBottom: "1px solid #ddd"
								 }}>
									 <div style={{ flex: 3 }}>Product Name</div>
				<div style={{ flex: 1, textAlign: "center" }}>Quantity</div>
				<div style={{ flex: 1, textAlign: "right" }}>Total</div>
				</div>	

				{/* Rows */}
				{appsmith.model.items.map(item => (
					<div style={{
					display: "flex",
					padding: "10px 15px",
					borderBottom: "1px solid #eee",
					alignItems: "center",
					color: 'white'
					}}>
					<div style={{ flex: 3 }}>{item.name}</div>
				<div style={{ flex: 1, textAlign: "center", color: 'white' }}>{item.quantity}</div>
				<div style={{ flex: 1, textAlign: "right", color: 'white' }}>${parseFloat(item.total).toFixed(2)}						</div>
			</div>
			))}
	</div>

);
}

appsmith.onReady(() => {
	/*
	 * This handler function will get called when parent application is ready.
	 * Initialize your component here
	 * more info - https://docs.appsmith.com/reference/widgets/custom#onready
	 */
	ReactDOM.render(<App />, document.getElementById("root"));
});
