SELECT TOP 50 Equipment_Code, Link_to_Client, Serial_Number, Make, Model, Description, Creation_Date, Location, Notes
FROM SICLOPS_LIVE_MVL.dbo.vw_LAYD_Equipment
WHERE (
		{{ Select1.selectedOptionValue ? `Link_to_Client = '${Select1.selectedOptionValue}'` : '1=1' }}
	)
AND (
		{{ Input1.text ? `Serial_Number LIKE '%${Input1.text}%'` : '1=1' }}
	);
